interface FontSize {
  /**
   * Tamaño de fuente extra pequeño (12px)
   */
  xs: string;

  /**
   * Tamaño de fuente pequeño (14px)
   */
  s: string;

  /**
   * Tamaño de fuente medio (16px)
   */
  m: string;

  /**
   * Tamaño de fuente grande (18px)
   */
  l: string;

  /**
   * Tamaño de fuente extra grande (20px)
   */
  xl: string;
}
