interface Spacing {
  /**
   * Tamaño de espaciado extra pequeño (4px)
   */
  xs: string;

  /**
   * Tamaño de espaciado pequeño (8px)
   */
  s: string;

  /**
   * Tamaño de espaciado medio (16px)
   */
  m: string;

  /**
   * Tamaño de espaciado grande (24px)
   */
  l: string;

  /**
   * Tamaño de espaciado extra grande (32px)
   */
  xl: string;
}
